import React from 'react'
import { Fragment, Component } from 'react'
import { Container, Row, Col } from 'react-bootstrap'
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";

class Testimonial extends Component{

    render(){

        var settings = {
            autoplay: true,
            autoplaySpeed: 3000,
            dots: true,
            infinite: false,
            vertical: true,
            verticalSwipping: true,
            speed: 3000,
            slidesToShow: 1,
            slidesToScroll: 1,
            responsive: [
              {
                breakpoint: 1024,
                settings: {
                  slidesToShow: 1,
                  slidesToScroll: 1,
                  infinite: true,
                  dots: true
                }
              },
              {
                breakpoint: 600,
                settings: {
                  slidesToShow: 1,
                  slidesToScroll: 1,
                  initialSlide: 1
                }
              },
              {
                breakpoint: 480,
                settings: {
                  slidesToShow: 1,
                  slidesToScroll: 1
                }
              }
            ]
          };


        return(
            <Fragment>
                <Container className="text-center">
                    <h2 className="heading-2 text-center">Testimonial</h2>
                    <Slider {...settings}>
                        <div>
                            <Row>
                                <Col lg={12}>
                                    <p>Emon</p>
                                </Col>
                            </Row>
                        </div>
                        <div>
                            <Row>
                                <Col lg={12}>
                                    <p>EmAhmedon</p>
                                </Col>
                            </Row>
                        </div>
                    </Slider>
                </Container>
            </Fragment>
        )
    }

}

export default Testimonial;
